package operators;

public class work3oprtr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int num1=10,num2=11;
    int x=num1|num2;
    System.out.println("x:"+x);
    int x1=num1&num2;
    System.out.println("x1:"+x1);
    int x2=num1^num2;
    System.out.println("x2:"+x2);
	}

}
