package typecasting;

public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
char val='A';
int num=val;
float res=num;
System.out.println(val+"\n"+num+"\n"+res);

System.out.println();
float res1=98.0f;
int num1=(int) res1;
char val1=(char) num1;
System.out.println(res1+"\n"+num1+"\n"+val1);
	}

}
